package Java;

import java.io.*;
import java.util.*;
import java.text.*;
import java.math.*;
import java.util.regex.*;

public class Java_EndOfFile 
{

	public static void main(String[] args)
	{
		// TODO Auto-generated method stub
		Scanner sc = new Scanner(System.in);
        int x = 0 ;
		while(sc.hasNext())
		
			
			System.out.println(++x + " " + sc.nextLine());
			
		
		sc.close();
		
 
	}

}